<script>
  //COOKIE
  let cookie = (name) => {
    let c = document.cookie.split('; ').find(cookie => cookie && cookie.startsWith(name + '='));
    return c ? c.split('=')[1] : false;
  }
  let clearCookies = () => {
    $('.cookies__checkbox').each((index, input) => {
      document.cookie = $(input).attr('id') + '=false';
    });
  }
  $('.cookies__button--agree-all').on('click', () => {
    $('.cookies__checkbox').each((index, input) => {
      document.cookie = $(input).attr('id') + '=true';
    });
    $('#cookies').addClass('d-none');
  });
  $('.cookies__button--agree-setting').on('click', () => {
    let $btn = $('.cookies__button--agree-setting');
    if ($btn.attr('data-step') === "1") {
      $('.cookies__button--agree-setting').html("Speichern");
      $('#cookies__settings').removeClass('d-none');
      $btn.attr('data-step', "2");
    } else {
      $('.cookies__checkbox').each((index, input) => {
        if ($(input).prop('checked')) {
          document.cookie = $(input).attr('id') + '=true';
        }
      });
      $('#cookies').addClass('d-none');
    }
  });
  $('.cookies__button--check-all').on('click', () => {
    $('.cookies__checkbox').each((index, input) => {
      $(input).prop('checked', true);
    });
  });
  $('.cookies__button--hide-details').on('click', () => {
    $('#cookies__settings').addClass('d-none');
    let $agreeSettingbtn = $('.cookies__button--agree-setting');
    $agreeSettingbtn.html('Einstellungen');
    $agreeSettingbtn.attr('data-step', "1");
  });
  let nocookies = true;
  $('.cookies__checkbox').each((index, input) => {
    if (cookie($(input).attr('id')) === "true") {
      nocookies = false;
    }
  });
  if (nocookies) {
    $('#cookies').removeClass('d-none');
  }
